/* eslint-disable */
const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const Cast = require('../../util/cast');
const StringUtil = require('../../util/string-util');
const {
    loadSound
} = require('../../import/load-sound.js');

/**
 * Icon svg to be displayed at the left edge of each extension block, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const blockIconURI = 'data:image/svg+xml;base64,';

/**
 * Icon svg to be displayed in the category menu, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const menuIconURI = 'data:image/svg+xml;base64,';

/**
 * @typedef {object} PenState - the pen state associated with a particular target.
 * @property {Boolean} penDown - tracks whether the pen should draw for this target.
 * @property {number} color - the current color (hue) of the pen.
 * @property {PenAttributes} penAttributes - cached pen attributes for the renderer. This is the authoritative value for
 *   diameter but not for pen color.
 */

/**
 * Host for the Pen-related blocks in Scratch 3.0
 * @param {Runtime} runtime - the runtime instantiating this block package.
 * @constructor
 */
class Scratch3LazyAudioBlocks {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;
        this._bufferedAudios = {};
    }

    _resetAudios () {
        this._bufferedAudios = {};
    }
    /**
     * @returns {object} metadata for this extension and its blocks.
     */
    getInfo () {
        return {
            id: 'lazyAudio',
            name: 'LazyAudio',
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [{
                opcode: 'load',
                blockType: BlockType.COMMAND,
                text: 'load([AUDIO_ID])',
                arguments: {
                    AUDIO_ID: {
                        type: ArgumentType.STRING,
                        defaultValue: '6ac484e97c1c1fe1384642e26a125e70.wav'
                    }
                }
            },
            {
                opcode: 'play',
                blockType: BlockType.COMMAND,
                text: 'play([AUDIO_ID])',
                arguments: {
                    AUDIO_ID: {
                        type: ArgumentType.STRING,
                        defaultValue: '6ac484e97c1c1fe1384642e26a125e70.wav'
                    }
                }
            },
            {
                opcode: 'playAndWait',
                blockType: BlockType.COMMAND,
                text: 'playAndWait([AUDIO_ID])',
                arguments: {
                    AUDIO_ID: {
                        type: ArgumentType.STRING,
                        defaultValue: '6ac484e97c1c1fe1384642e26a125e70.wav'
                    }
                }
            }
            ],
            menus: {}
        };
    }

    load (args, util) {
        const audioId = args.AUDIO_ID;
        if (!this._bufferedAudios[audioId]) {
            const sound = {
                md5: audioId
            };
            const soundBank = util.target.sprite.soundBank;
            return loadSound(sound, this.runtime, soundBank).then(() => {
                this._bufferedAudios[audioId] = sound;
            });
        }
    }

    play (args, util) {
        const audioId = args.AUDIO_ID;
        const sound = this._bufferedAudios[audioId];
        if (!sound) return;

        const soundBank = util.target.sprite.soundBank;
        if (!soundBank.soundPlayers[sound.soundId]) {
            loadSound(sound, this.runtime, soundBank).then(() => soundBank.playSound(util.target, sound.soundId));
            return;
        }
        soundBank.playSound(util.target, sound.soundId);
    }

    playAndWait (args, util) {
        const audioId = args.AUDIO_ID;
        const sound = this._bufferedAudios[audioId];
        if (!sound) return;

        const soundBank = util.target.sprite.soundBank;
        if (!soundBank.soundPlayers[sound.soundId]) {
            return loadSound(sound, this.runtime, soundBank).then(() => soundBank.playSound(util.target, sound.soundId));
        }
        return soundBank.playSound(util.target, sound.soundId);
    }
}

module.exports = Scratch3LazyAudioBlocks;
